	const pollContext = useContext(PollContext);
	const {
		id,
		question,
		option1,
		option2,
		option3,
		option1vote,
		option2vote,
		option3vote,
	} = poll;

	const handleVote = e => {};

	return (
		<div className='card bg-light'>
			<h3 className='text-primary text-left'>
				{question}{' '}
				<span style={{ float: 'right' }} className={'badge badge-primary '}>
					Vote
				</span>
			</h3>
			<form>
				<ul className='list'>
					<li>
						<select className='radio'>
							<i className='far fa-circle'> {option1}</i>
							{'  '}
							{option1vote}
						</select>
					</li>
					<li>
						<i className='far fa-circle'> {option2}</i>
						{'  '}
						{option2vote}
					</li>
					<li>
						<i className='far fa-circle'> {option3}</i>
						{'  '}
						{option3vote}
					</li>
				</ul>
			</form>
		</div>
	);
};

export default PollItem;
