import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import PollContext from '../../context/poll/pollContext';

const PollItem = ({ poll }) => {
	const pollContext = useContext(PollContext);
	const {
		id,
		question,
		option1,
		option2,
		option3,
		option1vote,
		option2vote,
		option3vote,
	} = poll;

	const handleVote = e => {};

	return (
		<div className='card bg-light'>
				<form>
			<h3 className='text-primary text-left'>
				{question}{' '}
				<span style={{ float: 'right' }} className={'badge badge-primary'}>
					Vote
				</span>
			</h3>
				<ul className='list'>
					<li>
						<input type='radio' name='radio' value='opt1' />
						<i> {option1}</i>
						{'  '}
						{option1vote}
					</li>
					<li>
						<input type='radio' name='radio' value='opt2' />
						<i> {option2}</i>
						{'  '}
						{option2vote}
					</li>
					<li>
						<input type='radio' name='radio' value='opt3' />
						<i> {option3}</i>
						{'  '}
						{option3vote}
					</li>
				</ul>
			</form>
		</div>
	);
};

export default PollItem;
