import React, { Fragment, useContext } from 'react';
import PollContext from '../../context/poll/pollContext';
import PollState from '../../context/poll/PollState';

const Polls = () => {
	const pollContext = useContext(PollState);

	const { polls } = pollContext;

	return (
		<Fragment>
			{polls.map(poll => (
				<h3>{poll.question}</h3>
			))}
		</Fragment>
	);
};

export default Polls;
