  ADD_POLL,
  DELETE_POLL,
  SET_CURRENT,
  CLEAR_CURRENT,
  UPDATE_POLL,
  FILTER_POLLS,
  CLEAR_FILTER,
} from '../types';

export default (state, action) => {
  switch(action.type) {
    case ADD_POLL:
      return {
        ...state,
        polls
      }
    default: return state
  }
}
