import React, { useContext } from 'react';
import PropTypes from 'prop-types';
import PollContext from '../../context/poll/pollContext';

const PollItem = ({ poll }) => {
	const pollContext = useContext(PollContext);
	const { setCurrent } = pollContext;
	const {
		id,
		question,
		option1,
		option2,
		option3,
		option1vote,
		option2vote,
		option3vote,
	} = poll;

	return (
		<div className='card bg-light'>
			<h3 className='text-primary text-left'>
				{question}{' '}
				<div
					style={{ float: 'right' }}
					className={'badge badge-primary'}
					onClick={() => setCurrent(poll)}
				>
					Vote
				</div>
			</h3>
			<ul className='list'>
				<li>
					<input type='radio' name='radio' value='opt1' />
					<i> {option1}</i>
					{'  '}
					{option1vote}
				</li>
				<li>
					<input type='radio' name='radio' value='opt2' />
					<i> {option2}</i>
					{'  '}
					{option2vote}
				</li>
				<li>
					<input type='radio' name='radio' value='opt3' />
					<i> {option3}</i>
					{'  '}
					{option3vote}
				</li>
			</ul>
		</div>
	);
};

export default PollItem;
